import { createStore, combineReducers, Store } from 'redux'
import { persistStore, Persistor, persistReducer } from 'redux-persist'
import DefaultStorage from 'redux-persist/lib/storage'
import userReducer, { User } from '../reducers/user';

import ActionTypes from './types';
import CustomCookieStorage from '../../utils/helpers/customCookieStorage';
import dataReducer, { Data } from '../reducers/data';

export interface StoreState {
  user: User,
  dataLocal: Data,
  dataCookie: Data
}

export type CustomReducer<T> = (state: T, action: { type: ActionTypes, payload: any }) => T

export function initializeStore(req?: any, res?: any): { store: Store, persistor: Persistor } {

  // pass to server
  const persistCookieConfig = {
    key: 'root',
    storage: CustomCookieStorage(req, res)
  }


  // used in client local storage
  const persistLocalConfig = {
    key: 'local',
    storage: DefaultStorage
  }

  const cookieConfigReducer = persistReducer(
    persistCookieConfig,
    combineReducers(
      {
        user: userReducer,
        dataCookie: dataReducer
      }
    )
  )

  const localConfigReducer = persistReducer(
    persistLocalConfig,
    combineReducers({
      dataLocal: dataReducer,
    })
  )

  const store: Store = createStore(
    // combineReducers<StoreState>({ user: userReducer })
    combineReducers({ ...cookieConfigReducer, ...localConfigReducer })
  )
  const persistor = persistStore(store, {})

  return { store, persistor }
}
