import React from 'react'
import { StoreState } from '../src/redux/config';
import { connect } from 'react-redux'
import { Dispatch } from 'redux';
import { PersistGate } from 'redux-persist/integration/react';
import { Persistor } from 'redux-persist';
import persistConnect from '../src/hocs/persist-connect';
import Link from 'next/link'
import protectedRoute from '../src/hocs/ProtectedRoute';
import ActionTypes from '../src/redux/config/types';
import AdminIndex from '../components/admin';
import SuperadminIndex from '../components/superadmin';

interface AppProps {
  user: number,
  persistor: Persistor,
  role: string,
  data: number
}

interface AppDispatch {
  increment(),
  dispatch: Dispatch
}

class App extends React.Component<AppProps & AppDispatch> {

  constructor(props) {
    super(props)
    console.log(typeof window !== 'undefined' ? 'Client' : 'Server', ' constructor')

  }
  render() {
    return (
      <>
        <hr></hr>
        <Link href="/login"><a style={{ margin: '20px' }}>Login</a></Link>
        <hr></hr>

        <h6>Hello - {this.props.role}</h6>
        <button className="btn btn-sm btn-primary" onClick={() => { this.props.increment() }}>Change Store</button>
        <div>Cookie Persist Sample - {this.props.user}</div>
        <div>Local Persist Sample - {this.props.data}</div>

        {this.props.role === 'admin' &&
          <AdminIndex />
        }

        {this.props.role === 'superadmin' &&
          <SuperadminIndex />
        }

      </>

    )
  }
}


const mapStateToProps = (state: StoreState) => {
  return {
    user: state.user.total,
    role: state.user.role,
    data: state.data.records
  }
}

const mapDispatchToProps = (dispatch: Dispatch) => {
  return {
    increment: () => {
      console.log("dispatching")
      dispatch({ type: ActionTypes.INCREMENT })
    }
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(App)
